import CartModal from "../UI/Modal/CartModal";
import classes from "./Cart.module.css";

const Cart = () => {

    const cartItems = (
    <ul className={classes['cart-items']}> {[{id: 'c1', name: 'Sushi', amount: 2, price: 12.99}, {id: 'c2', name: 'Shnitzel', amount: 1, price: 19.99}.map((item) => <li>{item.name}</li>)]}</ul>);

    return (
        <CartModal>

        {cartItems}
        <div className={classes.total}>
            <span>Total:</span>
            <span>37.43</span>
        </div>
        <div className={classes.actions}>
            <button className={classes['button-alt']}>Close</button>
            <button className={classes.button}>Order</button>
        </div>

    </CartModal>
    )

};

export default Cart;
