import Card from "../UI/Cards/Card";

const MealItem = (props) => {

    const mealsList = props.DUMMY_MEALS.map(meal => {
       return `<Card><li key={meal.id}><h3>{meal.name}</h3>
       <p>{meal.description}</p>
       <p>£{meal.price}</p></li></Card>`

    });

    return ( {mealsList}) ;
}

export default MealItem;
